global seq;global title_edit;global bits;function PutParameter(obj)  global title_edit;  global param;  global seq;  global bits;  seq = []  param = get(title_edit,'string');  for (i = 1 : length(param))    if(param(i) != " ")       seq = [seq str2num(param(i))]      bits = true(1);    endif
  endfor    for (i=1 : length(seq))    if(seq(i) == 1 || seq(i) == 0)      continue;    else       bits = false;    endif  endfor  endfunction                function bottonASK(obj)  global bits;  global seq;  PutParameter(obj);  if(length(seq) >= 8 && length(seq) <= 16 && bits)    close;    Modulacion_ASK(seq);  else    h = warndlg("No se puede ingresar estos valores", "Error de input");  endifendfunctionfunction bottonFSK(obj)  global bits;  global seq;  PutParameter(obj);  if(length(seq) >= 8 && length(seq) <= 16 && bits)    close;    Modulacion_FSK(seq);  else     h = warndlg("No se puede ingresar estos valores", "Error de input");  endifendfunctionfunction bottonPSK(obj)  global bits;  global seq;  PutParameter(obj);  if(length(seq) >= 8 && length(seq) <= 16 && bits)    close;    Modulacion_PSK(seq);  else    h = warndlg("No se puede ingresar estos valores", "Error de input");  endifendfunctionfunction update_plot (obj, init = false)      h = guidata (obj);  replot = false;  recalc = false;  endfunction               title = uipanel ("title", "Proyecto Integrador: Modulaciones", "position", [.25 .25 .5 .5]);title_text = uicontrol ("style", "text",                               "units", "normalized",                               "string", "Ingresar dígitos: ",                               "callback", @update_plot,                               "position", [0.3 0.5 0.35 0.06]);                               title_edit = uicontrol ("style", "edit",                               "units", "normalized",                               "string", "1 0 1 0 1 0 1 0",                               "callback", @PutParameter,                               "position", [0.35 0.4 0.35 0.06]);                               pushbutton_ASK = uicontrol ("style", "pushbutton",                                "units", "normalized",                                "string", "ASK",                                "callback", @bottonASK,                                "position", [0.45 0.3 0.08 0.05]);pushbutton_FSK = uicontrol ("style", "pushbutton",                                "units", "normalized",                                "string", "FSK",                                "callback", @bottonFSK,                                "position", [0.3 0.3 0.08 0.05]);pushbutton_PSK = uicontrol ("style", "pushbutton",                                "units", "normalized",                                "string", "PSK",                                "callback", @bottonPSK,                                "position", [0.6 0.3 0.08 0.05]);