global seq;global title_edit;function PutParameter(obj)  global title_edit;  global param;  global seq;  seq = []  param = get(title_edit,'string');  for (i = 1 : length(param))    if(param(i) != " ")       seq = [seq str2num(param(i))]    endif  endforendfunction                function bottonASK(obj)  global seq;  PutParameter(obj);  close;  Modulacion_ASK(seq);endfunctionfunction bottonFSK(obj)  global seq;  PutParameter(obj);  close;  Modulacion_FSK(seq);endfunctionfunction bottonPSK(obj)  global seq;  PutParameter(obj);  close;  Modulacion_PSK(seq);endfunctionfunction update_plot (obj, init = false)      h = guidata (obj);  replot = false;  recalc = false;  endfunction               title = uipanel ("title", "Proyecto Integrador: Modulaciones", "position", [.25 .25 .5 .5]);title_text = uicontrol ("style", "text",                               "units", "normalized",                               "string", "Ingresar dígitos: ",                               "callback", @update_plot,                               "position", [0.3 0.5 0.35 0.06]);                               title_edit = uicontrol ("style", "edit",                               "units", "normalized",                               "string", "1 0 1 0 1 0 1 0",                               "callback", @PutParameter,                               "position", [0.35 0.4 0.35 0.06]);                               pushbutton_ASK = uicontrol ("style", "pushbutton",                                "units", "normalized",                                "string", "ASK",                                "callback", @bottonASK,                                "position", [0.45 0.3 0.08 0.05]);pushbutton_FSK = uicontrol ("style", "pushbutton",                                "units", "normalized",                                "string", "FSK",                                "callback", @bottonFSK,                                "position", [0.3 0.3 0.08 0.05]);pushbutton_PSK = uicontrol ("style", "pushbutton",                                "units", "normalized",                                "string", "PSK",                                "callback", @bottonPSK,                                "position", [0.6 0.3 0.08 0.05]);