clear h                global pushbutton_ASK;                                          function bottonASK(obj)  close;  Modulacion_ASK;endfunctionfunction update_plot (obj, init = false)      h = guidata (obj);  replot = false;  recalc = false;    switch (gcbo)      case {h.print_pushbutton_ASK}        figure(2);        Modulacion_ASK;      case {h.print_pushbutton_FSK}        figure(2);        Modulacion_FSK;      case {h.print_pushbutton_PSK}        figure(2);        Modulacion_PSK;  endswitch      endfunction               title = uipanel ("title", "Proyecto Integrador: Modulaciones", "position", [.25 .25 .5 .5]);title_text = uicontrol ("style", "text",                               "units", "normalized",                               "string", "Ingresar dígitos: ",                               "callback", @update_plot,                               "position", [0.3 0.5 0.35 0.06]);                               title_edit = uicontrol ("style", "edit",                               "units", "normalized",                               "string", " ",                               "callback", @update_plot,                               "position", [0.35 0.4 0.35 0.06]);                               pushbutton_ASK = uicontrol ("style", "pushbutton",                                "units", "normalized",                                "string", "ASK",                                "callback", @bottonASK,                                "position", [0.45 0.3 0.08 0.05]);pushbutton_FSK = uicontrol ("style", "pushbutton",                                "units", "normalized",                                "string", "FSK",                                "callback", @update_plot,                                "position", [0.3 0.3 0.08 0.05]);pushbutton_PSK = uicontrol ("style", "pushbutton",                                "units", "normalized",                                "string", "PSK",                                "callback", @update_plot,                                "position", [0.6 0.3 0.08 0.05]);