clear h                global param;function PutParameter()  global title_edit;  global param;  param = get(title_edit , 'string');  printf("%s" , param);   endfunction                                function bottonASK(obj)  global param = 1 ;  close;  Modulacion_ASK(param);endfunctionfunction bottonFSK(obj)  close;  Modulacion_FSK;endfunctionfunction bottonPSK(obj)  close;  Modulacion_PSK;endfunctionfunction update_plot (obj, init = false)      h = guidata (obj);  replot = false;  recalc = false;  endfunction               title = uipanel ("title", "Proyecto Integrador: Modulaciones", "position", [.25 .25 .5 .5]);title_text = uicontrol ("style", "text",                               "units", "normalized",                               "string", "Ingresar dígitos: ",                               "callback", @update_plot,                               "position", [0.3 0.5 0.35 0.06]);                               title_edit = uicontrol ("style", "edit",                               "units", "normalized",                               "string", "1 0 1 0 1 0 1 0",                               "callback", @PutParameter,                               "position", [0.35 0.4 0.35 0.06]);                               pushbutton_ASK = uicontrol ("style", "pushbutton",                                "units", "normalized",                                "string", "ASK",                                "callback", @bottonASK,                                "position", [0.45 0.3 0.08 0.05]);pushbutton_FSK = uicontrol ("style", "pushbutton",                                "units", "normalized",                                "string", "FSK",                                "callback", @bottonFSK,                                "position", [0.3 0.3 0.08 0.05]);pushbutton_PSK = uicontrol ("style", "pushbutton",                                "units", "normalized",                                "string", "PSK",                                "callback", @bottonPSK,                                "position", [0.6 0.3 0.08 0.05]);