close allclear hgraphics_toolkit qt                function update_plot (obj, init = false)    h = guidata (obj);  replot = false;  recalc = false;    switch (gcbo)      case {h.print_pushbutton_ASK}        figure(2);        Modulacion_ASK;  endswitch        endfunction               h.title = uipanel ("title", "Ingrese digitos", "position", [.25 .25 .5 .5]);h.plot_title_edit = uicontrol ("style", "edit",                               "units", "normalized",                               "string", "Please fill me! (edit)",                               "callback", @update_plot,                               "position", [0.6 0.80 0.35 0.06]);                               h.print_pushbutton_ASK = uicontrol ("style", "pushbutton",                                "units", "normalized",                                "string", "ASK",                                "callback", @update_plot,                                "position", [0.6 0.45 0.35 0.09]);                set (gcf, "color", get(0, "defaultuicontrolbackgroundcolor"))guidata (gcf, h)update_plot (gcf, true);