close allclear hgraphics_toolkit qt                function update_plot (obj, init = false)    h = guidata (obj);  replot = false;  recalc = false;    switch (gcbo)      case {h.print_pushbutton_ASK}        figure(2);        Modulacion_ASK;      case {h.print_pushbutton_FSK}        figure(2);        Modulacion_FSK;      case {h.print_pushbutton_PSK}        figure(2);        Modulacion_PSK;  endswitch      endfunction               h.title = uipanel ("title", "Proyecto Integrador: Modulaciones", "position", [.25 .25 .5 .5]);h.plot_title_text = uicontrol ("style", "text",                               "units", "normalized",                               "string", "Ingresar dígitos: ",                               "callback", @update_plot,                               "position", [0.3 0.5 0.35 0.06]);                               h.plot_title_edit = uicontrol ("style", "edit",                               "units", "normalized",                               "string", " ",                               "callback", @update_plot,                               "position", [0.35 0.4 0.35 0.06]);                               h.print_pushbutton_ASK = uicontrol ("style", "pushbutton",                                "units", "normalized",                                "string", "ASK",                                "callback", @update_plot,                                "position", [0.45 0.3 0.08 0.05]);h.print_pushbutton_FSK = uicontrol ("style", "pushbutton",                                "units", "normalized",                                "string", "FSK",                                "callback", @update_plot,                                "position", [0.3 0.3 0.08 0.05]);h.print_pushbutton_PSK = uicontrol ("style", "pushbutton",                                "units", "normalized",                                "string", "PSK",                                "callback", @update_plot,                                "position", [0.6 0.3 0.08 0.05]);                set (gcf, "color", get(0, "defaultuicontrolbackgroundcolor"))guidata (gcf, h)update_plot (gcf, true);